// components/host/CreateListing.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../utils/AuthContext';

const CreateListing = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    guests: '',
    images: [],
    specificRatings: { 
      cleanliness: 0, 
      communication: 0, 
      checkin: 0, 
      accuracy: 0, 
      location: 0, 
      value: 0 
    }
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    
    if (name.includes('specificRatings.')) {
      const field = name.split('.')[1];
      setFormData({ 
        ...formData, 
        specificRatings: { 
          ...formData.specificRatings, 
          [field]: Number(value) || 0 
        } 
      });
    } else if (name === 'images') {
      const imageFiles = files ? Array.from(files) : [];
      if (imageFiles.length === 0) return;
      
      setLoading(true);
      Promise.all(imageFiles.map(file => {
        return new Promise((resolve, reject) => {
          if (file.size > 5 * 1024 * 1024) { // 5MB limit
            reject(new Error(`File ${file.name} is too large. Maximum size is 5MB.`));
            return;
          }
          
          const reader = new FileReader();
          reader.onloadend = () => resolve(reader.result);
          reader.onerror = () => reject(new Error(`Failed to read file ${file.name}`));
          reader.readAsDataURL(file);
        });
      })).then(base64Images => {
        setFormData({ 
          ...formData, 
          images: [...formData.images, ...base64Images] 
        });
        setError(null);
      }).catch(err => {
        setError(err.message);
      }).finally(() => {
        setLoading(false);
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const removeImage = (indexToRemove) => {
    setFormData({
      ...formData,
      images: formData.images.filter((_, index) => index !== indexToRemove)
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    // Validation
    if (!formData.title.trim()) {
      setError('Title is required');
      setLoading(false);
      return;
    }
    if (!formData.description.trim()) {
      setError('Description is required');
      setLoading(false);
      return;
    }
    if (!formData.price || Number(formData.price) <= 0) {
      setError('Please enter a valid price');
      setLoading(false);
      return;
    }
    if (!formData.location.trim()) {
      setError('Location is required');
      setLoading(false);
      return;
    }
    if (!formData.guests || Number(formData.guests) <= 0) {
      setError('Please enter a valid number of guests');
      setLoading(false);
      return;
    }

    try {
      const config = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
      
      const listingData = {
        ...formData,
        createdBy: user.id,
        status: 'pending' // All host submissions start as pending
      };

      console.log('Creating listing with payload:', listingData);
      await axios.post(`${process.env.REACT_APP_API_URL}/api/listings`, listingData, config);
      
      alert('Listing created successfully! It will be reviewed by an admin before being published.');
      navigate('/host/listings');
    } catch (err) {
      console.error('Error creating listing:', err);
      setError('Failed to create listing: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-2xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">Create New Listing</h1>
        
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <p className="text-blue-800 text-sm">
            <strong>Note:</strong> Your listing will be submitted for admin review and approval before it becomes visible to guests.
          </p>
        </div>

        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-gray-700 font-semibold mb-2">Property Title *</label>
            <input
              name="title"
              type="text"
              value={formData.title}
              onChange={handleChange}
              placeholder="e.g., Beautiful apartment in city center"
              className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
              required
              maxLength="100"
            />
          </div>

          <div>
            <label className="block text-gray-700 font-semibold mb-2">Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Describe your property, amenities, and what makes it special..."
              className="w-full p-3 border rounded-lg h-32 focus:ring-2 focus:ring-red-500 focus:border-red-500"
              required
              maxLength="500"
            />
            <p className="text-sm text-gray-500 mt-1">{formData.description.length}/500 characters</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-gray-700 font-semibold mb-2">Price per Night ($) *</label>
              <input
                name="price"
                type="number"
                value={formData.price}
                onChange={handleChange}
                placeholder="100"
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
                required
                min="1"
              />
            </div>
            
            <div>
              <label className="block text-gray-700 font-semibold mb-2">Maximum Guests *</label>
              <input
                name="guests"
                type="number"
                value={formData.guests}
                onChange={handleChange}
                placeholder="4"
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
                required
                min="1"
                max="20"
              />
            </div>
          </div>

          <div>
            <label className="block text-gray-700 font-semibold mb-2">Location *</label>
            <input
              name="location"
              type="text"
              value={formData.location}
              onChange={handleChange}
              placeholder="e.g., New York, NY"
              className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
              required
            />
          </div>

          <div>
            <label className="block text-gray-700 font-semibold mb-2">Property Images</label>
            <input
              name="images"
              type="file"
              accept="image/*"
              multiple
              onChange={handleChange}
              className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
              disabled={loading}
            />
            <p className="text-sm text-gray-500 mt-1">Maximum file size: 5MB per image</p>
            
            {formData.images.length > 0 && (
              <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4">
                {formData.images.map((img, index) => (
                  <div key={index} className="relative">
                    <img 
                      src={img} 
                      alt={`Property ${index + 1}`} 
                      className="w-full h-24 object-cover rounded border"
                    />
                    <button
                      type="button"
                      onClick={() => removeImage(index)}
                      className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm hover:bg-red-600"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div>
            <h3 className="text-lg font-semibold mb-4">Property Ratings (Optional)</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(formData.specificRatings).map(([key, value]) => (
                <div key={key}>
                  <label className="block text-gray-700 font-medium mb-2 capitalize">
                    {key === 'checkin' ? 'Check-in Experience' : key} Rating (0-5)
                  </label>
                  <input
                    name={`specificRatings.${key}`}
                    type="number"
                    step="0.1"
                    value={value}
                    onChange={handleChange}
                    placeholder="0.0"
                    className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500 focus:border-red-500"
                    min="0"
                    max="5"
                  />
                </div>
              ))}
            </div>
          </div>

          <div className="pt-4">
            <button 
              type="submit" 
              className="w-full bg-red-500 text-white px-6 py-3 rounded-lg hover:bg-red-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
              disabled={loading}
            >
              {loading ? 'Creating Listing...' : 'Submit for Review'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateListing;
