// Modified UserDashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const UserDashboard = () => {
  const [reservations, setReservations] = useState([]);
  const [recentListings, setRecentListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch user's reservations
        const reservationsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/reservations`);
        const userReservations = reservationsRes.data.filter(r => r.createdBy === user?.id);
        setReservations(userReservations);

        // Fetch recent approved listings for browsing
        const listingsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings?status=approved`);
        setRecentListings(listingsRes.data.slice(0, 6));

        setLoading(false);
      } catch (err) {
        console.error('Error fetching user data:', err);
        setLoading(false);
      }
    };

    if (user) {
      fetchUserData();
    }
  }, [user]);

  if (loading) return <div className="container mx-auto px-4 py-8">Loading...</div>;

  const upcomingReservations = reservations.filter(r => new Date(r.checkIn) > new Date());
  const pastReservations = reservations.filter(r => new Date(r.checkOut) < new Date());

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Welcome back, {user?.name}!</h1>
      
      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-blue-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-blue-800">Total Trips</h3>
          <p className="text-3xl font-bold text-blue-600">{reservations.length}</p>
        </div>
        <div className="bg-green-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-green-800">Upcoming Trips</h3>
          <p className="text-3xl font-bold text-green-600">{upcomingReservations.length}</p>
        </div>
        <div className="bg-purple-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-purple-800">Past Trips</h3>
          <p className="text-3xl font-bold text-purple-600">{pastReservations.length}</p>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <Link 
          to="/locations" 
          className="bg-red-500 text-white p-6 rounded-lg shadow hover:bg-red-600 text-center block"
        >
          <h3 className="text-xl font-semibold">Find Places</h3>
          <p className="mt-2">Discover new destinations</p>
        </Link>
        <Link 
          to="/reservations" 
          className="bg-blue-500 text-white p-6 rounded-lg shadow hover:bg-blue-600 text-center block"
        >
          <h3 className="text-xl font-semibold">My Reservations</h3>
          <p className="mt-2">View all your bookings</p>
        </Link>
        <Link 
          to="/login" 
          onClick={() => {/* Could add profile edit functionality */}}
          className="bg-gray-500 text-white p-6 rounded-lg shadow hover:bg-gray-600 text-center block"
        >
          <h3 className="text-xl font-semibold">Account Settings</h3>
          <p className="mt-2">Manage your profile</p>
        </Link>
      </div>

      {/* Upcoming Trips */}
      {upcomingReservations.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-4">Upcoming Trips</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {upcomingReservations.map((reservation) => (
              <div key={reservation._id} className="border rounded-lg p-4 shadow bg-green-50">
                <h3 className="text-lg font-semibold">Reservation #{reservation._id.slice(-6)}</h3>
                <p className="text-gray-600">Listing ID: {reservation.listingId}</p>
                <div className="mt-2">
                  <p className="text-sm"><strong>Check-in:</strong> {new Date(reservation.checkIn).toLocaleDateString()}</p>
                  <p className="text-sm"><strong>Check-out:</strong> {new Date(reservation.checkOut).toLocaleDateString()}</p>
                  <p className="text-sm"><strong>Guests:</strong> {reservation.guests}</p>
                  <p className="text-lg font-bold text-green-600 mt-2">${reservation.totalCost}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Recent Listings - Browse More */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold">Discover New Places</h2>
          <Link 
            to="/locations" 
            className="text-red-500 hover:text-red-600 font-medium"
          >
            View all →
          </Link>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {recentListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg overflow-hidden shadow hover:shadow-lg transition-shadow">
              <img
                src={listing.images?.[0] ? `${process.env.REACT_APP_API_URL}/uploads/${listing.images[0]}` : 'https://via.placeholder.com/300x200?text=Image+Not+Found'}
                alt={listing.title}
                className="w-full h-48 object-cover"
                onError={(e) => { e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found'; }}
              />
              <div className="p-4">
                <h3 className="text-lg font-semibold truncate">{listing.title}</h3>
                <p className="text-gray-600 text-sm">{listing.location}</p>
                <p className="text-lg font-bold text-red-600 mt-2">${listing.price}/night</p>
                <Link 
                  to={`/locations/${listing._id}`}
                  className="mt-3 bg-red-500 text-white px-4 py-2 rounded text-sm hover:bg-red-600 inline-block"
                >
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Past Trips */}
      {pastReservations.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-4">Past Trips</h2>
          <div className="space-y-4">
            {pastReservations.slice(0, 3).map((reservation) => (
              <div key={reservation._id} className="border rounded-lg p-4 shadow bg-gray-50">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-semibold">Reservation #{reservation._id.slice(-6)}</h3>
                    <p className="text-gray-600">Listing ID: {reservation.listingId}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(reservation.checkIn).toLocaleDateString()} - {new Date(reservation.checkOut).toLocaleDateString()}
                    </p>
                  </div>
                  <div className="text-right">
                    <p className="text-lg font-bold">${reservation.totalCost}</p>
                    <p className="text-sm text-gray-500">{reservation.guests} guests</p>
                  </div>
                </div>
              </div>
            ))}
            {pastReservations.length > 3 && (
              <Link 
                to="/reservations" 
                className="text-blue-500 hover:text-blue-600 text-sm font-medium"
              >
                View all past trips →
              </Link>
            )}
          </div>
        </div>
      )}

      {/* No Reservations State */}
      {reservations.length === 0 && (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">No trips yet</h3>
          <p className="text-gray-600 mb-6">Start planning your next adventure!</p>
          <Link 
            to="/locations" 
            className="bg-red-500 text-white px-6 py-3 rounded-lg hover:bg-red-600"
          >
            Browse Listings
          </Link>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
