import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../../utils/AuthContext';

const ViewListings = () => {
  const [listings, setListings] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all'); // pending, approved, rejected, all
  const { token } = useAuth();

  // Helper function to get image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  useEffect(() => {
    const fetchHostListings = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/host/listings`, config);
        console.log('Host listings response:', res.data);
        setListings(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching host listings:', err.response?.data || err.message);
        setError('Failed to fetch your listings');
        setLoading(false);
      }
    };
    fetchHostListings();
  }, [token]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredListings = listings.filter(listing => {
    if (filter === 'all') return true;
    return (listing.status || 'pending') === filter;
  });

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading your listings...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Your Listings</h1>
        <Link 
          to="/host/create-listing"
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Create New Listing
        </Link>
      </div>

      {/* Filter Buttons */}
      <div className="mb-6">
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setFilter('all')}
            className={`px-4 py-2 rounded ${
              filter === 'all' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            All ({listings.length})
          </button>
          <button
            onClick={() => setFilter('pending')}
            className={`px-4 py-2 rounded ${
              filter === 'pending' 
                ? 'bg-yellow-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Pending ({listings.filter(l => (l.status || 'pending') === 'pending').length})
          </button>
          <button
            onClick={() => setFilter('approved')}
            className={`px-4 py-2 rounded ${
              filter === 'approved' 
                ? 'bg-green-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Approved ({listings.filter(l => l.status === 'approved').length})
          </button>
          <button
            onClick={() => setFilter('rejected')}
            className={`px-4 py-2 rounded ${
              filter === 'rejected' 
                ? 'bg-red-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Rejected ({listings.filter(l => l.status === 'rejected').length})
          </button>
        </div>
      </div>

      {/* Status Information */}
      <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <h3 className="text-lg font-semibold text-blue-800 mb-2">Listing Status Information</h3>
        <ul className="text-sm text-blue-700 space-y-1">
          <li><span className="font-medium">Pending:</span> Your listing is waiting for admin approval</li>
          <li><span className="font-medium">Approved:</span> Your listing is live and visible to guests</li>
          <li><span className="font-medium">Rejected:</span> Your listing needs changes - you can edit and resubmit</li>
        </ul>
      </div>

      {/* Listings Grid */}
      {filteredListings.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">
            {listings.length === 0 
              ? "No listings yet" 
              : `No ${filter} listings found`
            }
          </h3>
          <p className="text-gray-600 mb-6">
            {listings.length === 0 
              ? "Create your first listing to start hosting guests"
              : `You don't have any ${filter} listings at the moment.`
            }
          </p>
          {listings.length === 0 && (
            <Link 
              to="/host/create-listing"
              className="bg-red-500 text-white px-6 py-3 rounded-lg hover:bg-red-600"
            >
              Create Your First Listing
            </Link>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg overflow-hidden shadow-lg bg-white">
              {/* Image and Status */}
              <div className="relative">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={getImageUrl(listing.images[0])}
                    alt={listing.title}
                    className="w-full h-48 object-cover"
                    onError={(e) => { 
                      e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                    }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <span className={`absolute top-2 right-2 px-3 py-1 text-sm rounded-full font-medium ${getStatusColor(listing.status || 'pending')}`}>
                  {(listing.status || 'pending').charAt(0).toUpperCase() + (listing.status || 'pending').slice(1)}
                </span>
              </div>

              {/* Listing Details */}
              <div className="p-4">
                <h3 className="text-lg font-semibold mb-2 truncate">{listing.title}</h3>
                <p className="text-gray-600 text-sm mb-2">{listing.location}</p>
                <p className="text-lg font-bold text-red-600 mb-2">${listing.price}/night</p>
                <p className="text-sm text-gray-700 mb-2">Max guests: {listing.guests}</p>

                <div className="text-xs text-gray-500 mb-4">
                  <p>Created: {new Date(listing.createdAt).toLocaleDateString()}</p>
                  {listing.updatedAt && listing.updatedAt !== listing.createdAt && (
                    <p>Updated: {new Date(listing.updatedAt).toLocaleDateString()}</p>
                  )}
                </div>

                {/* Status Messages */}
                {listing.status === 'pending' && (
                  <div className="mb-4 p-2 bg-yellow-50 border border-yellow-200 rounded text-sm text-yellow-800">
                    Your listing is under review. You'll be notified once it's approved.
                  </div>
                )}

                {listing.status === 'rejected' && (
                  <div className="mb-4 p-2 bg-red-50 border border-red-200 rounded text-sm text-red-800">
                    Your listing was rejected. Please make necessary changes and resubmit.
                  </div>
                )}

                {listing.status === 'approved' && (
                  <div className="mb-4 p-2 bg-green-50 border border-green-200 rounded text-sm text-green-800">
                    Your listing is live and accepting bookings!
                  </div>
                )}

                {/* Action Buttons */}
                <div className="flex gap-2 mt-4">
                  <Link 
                    to={`/host/listings/${listing._id}/edit`}
                    className="flex-1 bg-blue-500 text-white py-2 px-3 rounded text-sm text-center hover:bg-blue-600"
                  >
                    Edit
                  </Link>
                  <Link 
                    to={`/locations/${listing._id}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex-1 bg-gray-500 text-white py-2 px-3 rounded text-sm text-center hover:bg-gray-600"
                  >
                    View
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary Stats */}
      {listings.length > 0 && (
        <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-gray-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-gray-800">Total</h4>
            <p className="text-2xl font-bold text-gray-600">{listings.length}</p>
          </div>
          <div className="bg-yellow-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-yellow-800">Pending</h4>
            <p className="text-2xl font-bold text-yellow-600">
              {listings.filter(l => (l.status || 'pending') === 'pending').length}
            </p>
          </div>
          <div className="bg-green-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-green-800">Approved</h4>
            <p className="text-2xl font-bold text-green-600">
              {listings.filter(l => l.status === 'approved').length}
            </p>
          </div>
          <div className="bg-red-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-red-800">Rejected</h4>
            <p className="text-2xl font-bold text-red-600">
              {listings.filter(l => l.status === 'rejected').length}
            </p>
          </div>
        </div>
      )}

      {/* Back to Dashboard */}
      <div className="mt-8 text-center">
        <Link 
          to="/host"
          className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
        >
          Back to Host Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ViewListings;
