import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';
import { Link } from 'react-router-dom';

const ViewListings = () => {
  const [pendingListings, setPendingListings] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const { token } = useAuth();

  useEffect(() => {
    const fetchPendingListings = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings?status=pending`, config);
        setPendingListings(res.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch listings');
        setLoading(false);
      }
    };
    if (token) {
      fetchPendingListings();
    } else {
      setError('Authentication required');
      setLoading(false);
    }
  }, [token]);

  const handleRefresh = async () => {
    setLoading(true);
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings?status=pending`, config);
      setPendingListings(res.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to refresh listings');
      setLoading(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Listing Review Requests</h1>
        <div className="flex gap-4 items-center">
          <div className="text-sm text-gray-600">
            {pendingListings.length} listing(s) awaiting review
          </div>
          <button
            onClick={handleRefresh}
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            Refresh
          </button>
        </div>
      </div>

      {pendingListings.length === 0 ? (
        <div className="text-center py-8 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">No pending listings</h3>
          <p className="text-gray-600">There are no listings awaiting review at the moment.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {pendingListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg p-4 shadow bg-white">
              <img
                src={getImageUrl(listing.images?.[0])}
                alt={listing.title}
                className="w-full h-48 object-cover rounded-lg mb-4"
              />
              <div className="mb-4">
                <h3 className="text-lg font-semibold mb-2">{listing.title}</h3>
                <p className="text-gray-600 text-sm mb-2">{listing.location}</p>
                <p className="text-lg font-bold text-blue-600">${listing.price}/night</p>
                <div className="text-sm text-gray-600 mb-4">
                  <p><strong>Host:</strong> {listing.createdBy?.name || 'Unknown'}</p>
                  <p><strong>Email:</strong> {listing.createdBy?.email || 'N/A'}</p>
                  <p><strong>Submitted:</strong> {new Date(listing.createdAt).toLocaleDateString()}</p>
                </div>
                <p className="text-sm text-gray-700 mb-4 line-clamp-3">
                  {listing.description?.length > 80
                    ? `${listing.description.substring(0, 80)}...`
                    : listing.description}
                </p>
                <Link
                  to={`/admin/listings/${listing._id}/review`}
                  className="block w-full bg-blue-500 text-white py-3 px-4 rounded hover:bg-blue-600 text-center font-medium"
                >
                  Review Listing
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}

      {pendingListings.length > 0 && (
        <div className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-800 mb-2">Review Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">{pendingListings.length}</p>
              <p className="text-yellow-700">Listings to Review</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">
                {pendingListings.filter(
                  (l) => new Date(l.createdAt) > new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
                ).length}
              </p>
              <p className="text-yellow-700">This Week</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">
                {pendingListings.filter(
                  (l) => new Date(l.createdAt) > new Date(Date.now() - 24 * 60 * 60 * 1000)
                ).length}
              </p>
              <p className="text-yellow-700">Today</p>
            </div>
          </div>
        </div>
      )}

      <div className="mt-8 text-center">
        <Link
          to="/admin"
          className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
        >
          Back to Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ViewListings;
