import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';
import { Link } from 'react-router-dom';

const ViewListings = () => {
  const [pendingListings, setPendingListings] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const { token } = useAuth();

  useEffect(() => {
    const fetchPendingListings = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings`, config);
        console.log('All listings response:', res.data);

        // Filter to show only pending listings
        const pending = res.data.filter(listing => listing.status === 'pending');
        setPendingListings(pending);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching listings:', err.response?.data || err.message);
        setError('Failed to fetch listings');
        setLoading(false);
      }
    };
    fetchPendingListings();
  }, [token]);

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Listing Review Requests</h1>
        <div className="text-sm text-gray-600">
          {pendingListings.length} listing(s) awaiting review
        </div>
      </div>

      {/* Pending Listings */}
      {pendingListings.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">No pending listings</h3>
          <p className="text-gray-600">
            There are no listings waiting for approval at the moment.
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {pendingListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg overflow-hidden shadow-lg bg-white">
              {/* Status Badge */}
              <div className="relative">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={getImageUrl(listing.images[0])}
                    alt={listing.title}
                    className="w-full h-48 object-cover"
                    onError={(e) => { 
                      e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                    }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <span className="absolute top-2 right-2 px-3 py-1 text-sm rounded-full font-medium bg-yellow-100 text-yellow-800">
                  Pending Review
                </span>
              </div>

              {/* Listing Details */}
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-2 truncate">{listing.title}</h2>
                <p className="text-gray-600 mb-2">{listing.location}</p>
                <p className="text-lg font-bold text-red-600 mb-2">${listing.price}/night</p>
                <p className="text-sm text-gray-700 mb-2">Max guests: {listing.guests}</p>

                {/* Host Information */}
                <div className="text-sm text-gray-600 mb-4">
                  <p><strong>Host:</strong> {listing.createdBy?.name || 'Unknown'}</p>
                  <p><strong>Email:</strong> {listing.createdBy?.email || 'N/A'}</p>
                  <p><strong>Submitted:</strong> {new Date(listing.createdAt).toLocaleDateString()}</p>
                </div>

                {/* Description Preview */}
                <p className="text-sm text-gray-700 mb-4 line-clamp-3">
                  {listing.description?.length > 80 
                    ? `${listing.description.substring(0, 80)}...` 
                    : listing.description
                  }
                </p>

                {/* Review Button */}
                <Link 
                  to={`/admin/listings/${listing._id}/review`}
                  className="block w-full bg-blue-500 text-white py-3 px-4 rounded hover:bg-blue-600 text-center font-medium"
                >
                  Review Listing
                </Link>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Quick Stats */}
      {pendingListings.length > 0 && (
        <div className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-800 mb-2">Review Summary</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">{pendingListings.length}</p>
              <p className="text-yellow-700">Listings to Review</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">
                {pendingListings.filter(l => 
                  new Date(l.createdAt) > new Date(Date.now() - 7 * 24 * 60 * 60 * 1000)
                ).length}
              </p>
              <p className="text-yellow-700">This Week</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-yellow-600">
                {pendingListings.filter(l => 
                  new Date(l.createdAt) > new Date(Date.now() - 24 * 60 * 60 * 1000)
                ).length}
              </p>
              <p className="text-yellow-700">Today</p>
            </div>
          </div>
        </div>
      )}

      {/* Navigation */}
      <div className="mt-8 text-center">
        <Link 
          to="/admin" 
          className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
        >
          Back to Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ViewListings;
