// Modified HostDashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const HostDashboard = () => {
  const [stats, setStats] = useState({
    totalListings: 0,
    pendingListings: 0,
    approvedListings: 0,
    rejectedListings: 0
  });
  const [approvedListings, setApprovedListings] = useState([]);
  const [pendingListings, setPendingListings] = useState([]);
  const [rejectedListings, setRejectedListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, token } = useAuth();

  useEffect(() => {
    const fetchHostData = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const listingsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings`, config);
        const hostListings = listingsRes.data.filter(listing => listing.createdBy?._id === user?.id);
        
        const totalListings = hostListings.length;
        const pendingListings = hostListings.filter(l => l.status === 'pending');
        const approvedListings = hostListings.filter(l => l.status === 'approved');
        const rejectedListings = hostListings.filter(l => l.status === 'rejected');

        setStats({
          totalListings,
          pendingListings: pendingListings.length,
          approvedListings: approvedListings.length,
          rejectedListings: rejectedListings.length
        });

        setApprovedListings(approvedListings);
        setPendingListings(pendingListings);
        setRejectedListings(rejectedListings);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching host data:', err);
        setLoading(false);
      }
    };

    if (user && token) {
      fetchHostData();
    }
  }, [user, token]);

  const handleDeleteListing = async (listingId) => {
    if (window.confirm('Are you sure you want to delete this listing?')) {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        await axios.delete(`${process.env.REACT_APP_API_URL}/api/listings/${listingId}`, config);
        
        setApprovedListings(prev => prev.filter(listing => listing._id !== listingId));
        setPendingListings(prev => prev.filter(listing => listing._id !== listingId));
        setRejectedListings(prev => prev.filter(listing => listing._id !== listingId));
        setStats(prev => ({
          ...prev,
          totalListings: prev.totalListings - 1,
          approvedListings: prev.approvedListings - (approvedListings.some(l => l._id === listingId) ? 1 : 0),
          pendingListings: prev.pendingListings - (pendingListings.some(l => l._id === listingId) ? 1 : 0),
          rejectedListings: prev.rejectedListings - (rejectedListings.some(l => l._id === listingId) ? 1 : 0)
        }));
        
        alert('Listing deleted successfully!');
      } catch (err) {
        alert('Failed to delete listing: ' + (err.response?.data?.message || err.message));
      }
    }
  };

  if (loading) return <div className="container mx-auto px-4 py-8">Loading...</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Host Dashboard</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-blue-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-blue-800">Total Listings</h3>
          <p className="text-3xl font-bold text-blue-600">{stats.totalListings}</p>
        </div>
        <div className="bg-yellow-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-yellow-800">Pending Approval</h3>
          <p className="text-3xl font-bold text-yellow-600">{stats.pendingListings}</p>
        </div>
        <div className="bg-green-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-green-800">Approved Listings</h3>
          <p className="text-3xl font-bold text-green-600">{stats.approvedListings}</p>
        </div>
        <div className="bg-red-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-red-800">Rejected Listings</h3>
          <p className="text-3xl font-bold text-red-600">{stats.rejectedListings}</p>
        </div>
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-bold mb-4">Quick Actions</h2>
        <div className="flex flex-wrap gap-4">
          <Link 
            to="/host/create-listing"
            className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600"
          >
            Create New Listing
          </Link>
          <Link 
            to="/host/reservations"
            className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600"
          >
            View Reservations
          </Link>
          <Link 
            to="/host/listings/manage"
            className="bg-black text-white px-6 py-3 rounded-lg hover:bg-gray-800"
          >
            Manage Listings
          </Link>
        </div>
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-bold mb-4">Pending Listings</h2>
        {pendingListings.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <h3 className="text-xl font-semibold mb-2">No pending listings</h3>
            <p className="text-gray-600 mb-4">Create a new listing to get started.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {pendingListings.map((listing) => (
              <div key={listing._id} className="border rounded-lg p-4 shadow bg-white">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/uploads/${listing.images[0]}`}
                    alt={listing.title}
                    className="w-full h-48 object-cover rounded-lg mb-4"
                    onError={(e) => { e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found'; }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center rounded-lg mb-4">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <div className="mb-4">
                  <h3 className="text-lg font-semibold mb-2">{listing.title}</h3>
                  <p className="text-gray-600 text-sm mb-2">{listing.location}</p>
                  <p className="text-lg font-bold text-yellow-600">${listing.price}/night</p>
                  <p className="text-sm text-gray-500">Max {listing.guests} guests</p>
                  <p className="text-sm text-yellow-600">Status: {listing.status}</p>
                </div>
                <div className="flex gap-2">
                  <Link
                    to={`/host/listings/${listing._id}/edit`}
                    className="flex-1 bg-blue-500 text-white px-3 py-2 rounded text-center text-sm hover:bg-blue-600"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => handleDeleteListing(listing._id)}
                    className="flex-1 bg-red-500 text-white px-3 py-2 rounded text-sm hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-bold mb-4">Approved Listings</h2>
        {approvedListings.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <h3 className="text-xl font-semibold mb-2">No approved listings yet</h3>
            <p className="text-gray-600 mb-4">Create your first listing to get started.</p>
            <Link 
              to="/host/create-listing"
              className="bg-green-500 text-white px-6 py-3 rounded-lg hover:bg-green-600"
            >
              Create Your First Listing
            </Link>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {approvedListings.map((listing) => (
              <div key={listing._id} className="border rounded-lg p-4 shadow bg-white">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/uploads/${listing.images[0]}`}
                    alt={listing.title}
                    className="w-full h-48 object-cover rounded-lg mb-4"
                    onError={(e) => { e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found'; }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center rounded-lg mb-4">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <div className="mb-4">
                  <h3 className="text-lg font-semibold mb-2">{listing.title}</h3>
                  <p className="text-gray-600 text-sm mb-2">{listing.location}</p>
                  <p className="text-lg font-bold text-green-600">${listing.price}/night</p>
                  <p className="text-sm text-gray-500">Max {listing.guests} guests</p>
                  <p className="text-sm text-green-600">Status: {listing.status}</p>
                </div>
                <div className="flex gap-2">
                  <Link
                    to={`/host/listings/${listing._id}/edit`}
                    className="flex-1 bg-blue-500 text-white px-3 py-2 rounded text-center text-sm hover:bg-blue-600"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => handleDeleteListing(listing._id)}
                    className="flex-1 bg-red-500 text-white px-3 py-2 rounded text-sm hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="mb-8">
        <h2 className="text-2xl font-bold mb-4">Rejected Listings</h2>
        {rejectedListings.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <h3 className="text-xl font-semibold mb-2">No rejected listings</h3>
            <p className="text-gray-600 mb-4">No listings have been rejected.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {rejectedListings.map((listing) => (
              <div key={listing._id} className="border rounded-lg p-4 shadow bg-white">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={`${process.env.REACT_APP_API_URL}/Uploads/${listing.images[0]}`}
                    alt={listing.title}
                    className="w-full h-48 object-cover rounded-lg mb-4"
                    onError={(e) => { e.target.src = 'https://via.placeholder.com/300x200?text=Image+Not+Found'; }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center rounded-lg mb-4">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <div className="mb-4">
                  <h3 className="text-lg font-semibold mb-2">{listing.title}</h3>
                  <p className="text-gray-600 text-sm mb-2">{listing.location}</p>
                  <p className="text-lg font-bold text-red-600">${listing.price}/night</p>
                  <p className="text-sm text-gray-500">Max {listing.guests} guests</p>
                  <p className="text-sm text-red-600">Status: {listing.status}</p>
                  {listing.rejectionReason && (
                    <p className="text-sm text-red-500">Reason: {listing.rejectionReason}</p>
                  )}
                </div>
                <div className="flex gap-2">
                  <Link
                    to={`/host/listings/${listing._id}/edit`}
                    className="flex-1 bg-blue-500 text-white px-3 py-2 rounded text-center text-sm hover:bg-blue-600"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => handleDeleteListing(listing._id)}
                    className="flex-1 bg-red-500 text-white px-3 py-2 rounded text-sm hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default HostDashboard;
