import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';
import { getImageUrl } from '../../utils/imageUtils'; // Import the helper

const HostDashboard = () => {
  const [stats, setStats] = useState({
    totalListings: 0,
    approvedListings: 0,
    pendingListings: 0,
    totalReservations: 0,
    totalRevenue: 0
  });
  const [recentListings, setRecentListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const { token } = useAuth();

  useEffect(() => {
    const fetchHostData = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        
        // Fetch host's listings
        const listingsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/host/listings`, config);
        const listings = listingsRes.data;
        
        // Calculate stats (removed rejected listings)
        const stats = {
          totalListings: listings.length,
          approvedListings: listings.filter(l => l.status === 'approved').length,
          pendingListings: listings.filter(l => l.status === 'pending').length
        };
        
        // Fetch host reservations using the correct endpoint
        try {
          const reservationsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/reservations/host/all`, config);
          const hostReservations = reservationsRes.data;
          
          stats.totalReservations = hostReservations.length;
          stats.totalRevenue = hostReservations.reduce((sum, r) => sum + (r.totalCost || 0), 0);
        } catch (reservationError) {
          console.warn('Could not fetch host reservations:', reservationError.message);
          // Set default values if reservations can't be fetched
          stats.totalReservations = 0;
          stats.totalRevenue = 0;
        }
        
        setStats(stats);
        setRecentListings(listings.slice(0, 3)); // Show 3 most recent listings
        setLoading(false);
      } catch (err) {
        console.error('Error fetching host data:', err);
        
        // If it's an auth error, still try to show listing stats
        if (err.response?.status === 401) {
          console.warn('Authentication issue, showing limited data');
        }
        
        setLoading(false);
      }
    };

    if (token) {
      fetchHostData();
    }
  }, [token]);

  if (loading) return <div className="container mx-auto px-4 py-8">Loading...</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Host Dashboard</h1>
      
      {/* Stats Cards - Removed Rejected section */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-blue-100 p-6 rounded-lg shadow">
          <h3 className="text-xl font-semibold text-blue-800">Total Listings</h3>
          <p className="text-3xl font-bold text-blue-600">{stats.totalListings}</p>
        </div>
        <div className="bg-green-100 p-6 rounded-lg shadow">
          <h3 className="text-xl font-semibold text-green-800">Approved</h3>
          <p className="text-3xl font-bold text-green-600">{stats.approvedListings}</p>
        </div>
        <div className="bg-yellow-100 p-6 rounded-lg shadow">
          <h3 className="text-xl font-semibold text-yellow-800">Pending</h3>
          <p className="text-3xl font-bold text-yellow-600">{stats.pendingListings}</p>
        </div>
      </div>

      {/* Revenue Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
        <div className="bg-black text-white p-6 rounded-lg shadow">
          <h3 className="text-xl font-semibold">Total Reservations</h3>
          <p className="text-3xl font-bold">{stats.totalReservations}</p>
        </div>
        <div className="bg-green-600 text-white p-6 rounded-lg shadow">
          <h3 className="text-xl font-semibold">Total Revenue</h3>
          <p className="text-3xl font-bold">${stats.totalRevenue.toLocaleString()}</p>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <Link 
          to="/host/create-listing" 
          className="bg-red-500 text-white p-6 rounded-lg shadow hover:bg-red-600 text-center block transition-colors"
        >
          <h3 className="text-xl font-semibold">Create New Listing</h3>
          <p className="mt-2">Add a new property</p>
        </Link>
        <Link 
          to="/host/listings" 
          className="bg-blue-500 text-white p-6 rounded-lg shadow hover:bg-blue-600 text-center block transition-colors"
        >
          <h3 className="text-xl font-semibold">Manage Listings</h3>
          <p className="mt-2">View and edit your properties</p>
        </Link>
        <Link 
          to="/host/reservations" 
          className="bg-green-500 text-white p-6 rounded-lg shadow hover:bg-green-600 text-center block transition-colors"
        >
          <h3 className="text-xl font-semibold">View Reservations</h3>
          <p className="mt-2">Check your bookings</p>
        </Link>
      </div>

      {/* Recent Listings - Only shows when there are listings */}
      {recentListings.length > 0 && (
        <div>
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold">Your Recent Listings</h2>
            <Link 
              to="/host/listings" 
              className="text-red-500 hover:text-red-600 font-medium"
            >
              View all →
            </Link>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {recentListings.map((listing) => (
              <div key={listing._id} className="border rounded-lg overflow-hidden shadow">
                <div className="relative">
                  {listing.images && listing.images.length > 0 ? (
                    <img
                      src={getImageUrl(listing.images[0])} // Use the helper function
                      alt={listing.title}
                      className="w-full h-48 object-cover"
                      onError={(e) => { 
                        e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                      }}
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                      <span className="text-gray-500">No Image</span>
                    </div>
                  )}
                  <span className={`absolute top-2 right-2 px-2 py-1 text-xs rounded-full ${
                    listing.status === 'approved' ? 'bg-green-100 text-green-800' :
                    listing.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                    'bg-red-100 text-red-800'
                  }`}>
                    {listing.status}
                  </span>
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-semibold truncate">{listing.title}</h3>
                  <p className="text-gray-600 text-sm">{listing.location}</p>
                  <p className="text-lg font-bold text-red-600 mt-2">${listing.price}/night</p>
                  <div className="flex gap-2 mt-3">
                    <Link 
                      to={`/host/listings/${listing._id}/edit`}
                      className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600"
                    >
                      Edit
                    </Link>
                    <Link 
                      to={`/locations/${listing._id}`}
                      className="bg-gray-500 text-white px-3 py-1 rounded text-sm hover:bg-gray-600"
                    >
                      View
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default HostDashboard;
