import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../utils/AuthContext';
import { useNavigate } from 'react-router-dom';

const Reservations = () => {
  const [reservations, setReservations] = useState([]);
  const [error, setError] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [editForm, setEditForm] = useState({ checkIn: '', checkOut: '', guests: '' });
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/reservations`, config);
        console.log('Reservations response:', res.data);
        if (user) {
          if (user.email === 'admin@example.com') {
            setReservations(res.data.filter(r => r._id)); // Admin sees all
          } else if (user.id) {
            const userReservations = res.data.filter(r => r.createdBy?._id?.toString() === user.id);
            setReservations(userReservations); // Normal users see their own
          } else {
            setError('User ID not found, please log in again');
          }
        } else {
          setError('Please log in to view reservations');
        }
      } catch (err) {
        console.error('Error fetching reservations:', err.response?.data || err.message);
        setError(`Error fetching reservations: ${err.response?.status || ''} ${err.message}`);
      }
    };
    if (user) fetchReservations();
    else navigate('/login');
  }, [user, navigate]);

  const handleEdit = (reservation) => {
    setEditingId(reservation._id);
    setEditForm({
      checkIn: new Date(reservation.checkIn).toISOString().split('T')[0],
      checkOut: new Date(reservation.checkOut).toISOString().split('T')[0],
      guests: reservation.guests || 1,
    });
  };

  const handleSave = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const config = token ? { headers: { Authorization: `Bearer ${token}` } } : {};
      await axios.put(`${process.env.REACT_APP_API_URL}/api/reservations/${id}`, editForm, config);
      setReservations(reservations.map(r => r._id === id ? { ...r, ...editForm } : r));
      setEditingId(null);
    } catch (err) {
      console.error('Error updating reservation:', err.response?.data || err.message);
      setError('Error updating reservation');
    }
  };

  const handleChange = (e) => {
    setEditForm({ ...editForm, [e.target.name]: e.target.value });
  };

  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;
  if (!user) return null;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">{user.email === 'admin@example.com' ? 'All Reservations' : 'Your Reservations'}</h1>
      {reservations.length === 0 ? (
        <p className="text-gray-600 text-center">No reservations found.</p>
      ) : (
        <div className="flex flex-col gap-6">
          {reservations.map((reservation) => (
            <div key={reservation._id} className="border rounded-lg p-4 shadow">
              <h2 className="text-xl font-semibold">Reservation ID: {reservation._id}</h2>
              <p>Listing: {reservation.listingId?.title || 'N/A'}</p>
              {editingId === reservation._id && (user.email === 'admin@example.com' || reservation.createdBy?._id?.toString() === user.id) ? (
                <div className="mt-2">
                  <input
                    type="date"
                    name="checkIn"
                    value={editForm.checkIn}
                    onChange={handleChange}
                    className="border p-2 rounded mb-2"
                  />
                  <input
                    type="date"
                    name="checkOut"
                    value={editForm.checkOut}
                    onChange={handleChange}
                    className="border p-2 rounded mb-2 ml-2"
                  />
                  <input
                    type="number"
                    name="guests"
                    value={editForm.guests}
                    onChange={handleChange}
                    className="border p-2 rounded mb-2 ml-2"
                    min="1"
                  />
                  <button
                    onClick={() => handleSave(reservation._id)}
                    className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                  >
                    Save
                  </button>
                  <button
                    onClick={() => setEditingId(null)}
                    className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 ml-2"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <>
                  <p>Check-in: {new Date(reservation.checkIn).toLocaleDateString()}</p>
                  <p>Check-out: {new Date(reservation.checkOut).toLocaleDateString()}</p>
                  <p>Guests: {reservation.guests || 'N/A'}</p>
                  <p>Total Cost: ${reservation.totalCost || 'N/A'}</p>
                  <p>Created By: {reservation.createdBy?.email || 'Unknown'}</p>
                  <p>Created At: {new Date(reservation.createdAt).toLocaleString()}</p>
                  {(user.email === 'admin@example.com' || reservation.createdBy?._id?.toString() === user.id) && (
                    <button
                      onClick={() => handleEdit(reservation)}
                      className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 mt-2"
                    >
                      Edit
                    </button>
                  )}
                </>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Reservations;
