import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

const Locations = () => {
  const [listings, setListings] = useState([]);
  const [sortOption, setSortOption] = useState('price-asc');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const params = new URLSearchParams(location.search);
        const query = {
          location: params.get('location') || '',
          checkIn: params.get('checkIn') || '',
          checkOut: params.get('checkOut') || '',
          guests: params.get('guests') || '',
        };
        console.log('Raw query params:', location.search);
        console.log('Parsed query:', query);
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings`, { params: query });
        console.log('Listings response:', res.data);
        setListings(res.data);
      } catch (err) {
        console.error('Error fetching listings:', err.response?.data || err.message);
      }
    };
    fetchListings();
  }, [location.search]);

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
    let sortedListings = [...listings];
    switch (e.target.value) {
      case 'price-asc':
        sortedListings.sort((a, b) => a.price - b.price);
        break;
      case 'price-desc':
        sortedListings.sort((a, b) => b.price - a.price);
        break;
      case 'rating-desc':
        sortedListings.sort((a, b) => (b.specificRatings?.value || 0) - (a.specificRatings?.value || 0));
        break;
      default:
        break;
    }
    setListings(sortedListings);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Explore Listings</h1>
      <div className="mb-4">
        <label htmlFor="sort" className="mr-2">Sort by:</label>
        <select id="sort" value={sortOption} onChange={handleSortChange} className="p-2 border rounded">
          <option value="price-asc">Price: Low to High</option>
          <option value="price-desc">Price: High to Low</option>
          <option value="rating-desc">Rating: High to Low</option>
        </select>
      </div>
      <div className="flex flex-col gap-6">
        {listings.map((listing) => (
          <div key={listing._id} className="border rounded-lg p-4 shadow hover:shadow-lg transition-shadow">
            <img
              src={listing.images && listing.images.length > 0 ? listing.images[0] : 'https://via.placeholder.com/400'}
              alt={listing.title}
              className="w-full h-48 object-cover rounded-t-lg"
            />
            <div className="p-4">
              <h2 className="text-xl font-semibold">{listing.title}</h2>
              <p className="text-gray-600">{listing.location}</p>
              <p className="text-lg font-bold">${listing.price} / night</p>
              <div className="mt-2">
                <h3 className="text-md font-medium">Reviews:</h3>
                <ul className="list-disc pl-5">
                  <li>Cleanliness: {listing.specificRatings?.cleanliness || 'N/A'}</li>
                  <li>Communication: {listing.specificRatings?.communication || 'N/A'}</li>
                  <li>Check-in: {listing.specificRatings?.checkin || 'N/A'}</li>
                  <li>Accuracy: {listing.specificRatings?.accuracy || 'N/A'}</li>
                  <li>Location: {listing.specificRatings?.location || 'N/A'}</li>
                  <li>Value: {listing.specificRatings?.value || 'N/A'}</li>
                </ul>
              </div>
              <button
                onClick={() => navigate(`/locations/${listing._id}`)}
                className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              >
                View Details
              </button>
            </div>
          </div>
        ))}
        {listings.length === 0 && <p className="text-gray-600 text-center">No listings found for your search.</p>}
      </div>
    </div>
  );
};

export default Locations;
