import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../utils/AuthContext';

const CreateListing = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    guests: '',
    images: [],
    specificRatings: { 
      cleanliness: 0, 
      communication: 0, 
      checkin: 0, 
      accuracy: 0, 
      location: 0, 
      value: 0 
    }
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { token } = useAuth();

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    
    if (name.includes('specificRatings.')) {
      const field = name.split('.')[1];
      setFormData({ 
        ...formData, 
        specificRatings: { 
          ...formData.specificRatings, 
          [field]: Number(value) || 0 
        } 
      });
    } else if (name === 'images') {
      const imageFiles = files ? Array.from(files) : [];
      
      Promise.all(imageFiles.map(file => {
        return new Promise((resolve) => {
          const reader = new FileReader();
          reader.onloadend = () => {
            const img = new Image();
            img.src = reader.result;
            img.onload = () => {
              const canvas = document.createElement('canvas');
              const maxWidth = 800;
              const scale = maxWidth / img.width;
              canvas.width = maxWidth;
              canvas.height = img.height * scale;
              const ctx = canvas.getContext('2d');
              ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
              const compressed = canvas.toDataURL('image/jpeg', 0.7);
              resolve(compressed);
            };
          };
          reader.readAsDataURL(file);
        });
      })).then(base64Images => {
        setFormData({ ...formData, images: base64Images });
      });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      console.log('Creating listing with payload:', formData);
      
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/listings`, 
        formData, 
        config
      );
      
      console.log('Listing created:', res.data);
      alert('Listing created successfully! It will be reviewed by admin before going live.');
      navigate('/host');
    } catch (err) {
      console.error('Error creating listing:', err.response?.data || err.message);
      alert('Failed to create listing: ' + (err.response?.data?.message || err.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Create New Listing</h1>
        <Link 
          to="/host" 
          className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
        >
          Back to Dashboard
        </Link>
      </div>
      
      <div className="max-w-2xl mx-auto bg-white rounded-lg shadow-md p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          
          {/* Basic Information */}
          <div className="border-b pb-6">
            <h2 className="text-xl font-semibold mb-4">Basic Information</h2>
            
            <div className="mb-4">
              <label className="block text-gray-700 font-semibold mb-2">Property Title *</label>
              <input 
                name="title" 
                value={formData.title} 
                onChange={handleChange} 
                placeholder="e.g., Cozy Downtown Apartment" 
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
                required 
              />
            </div>
            
            <div className="mb-4">
              <label className="block text-gray-700 font-semibold mb-2">Description *</label>
              <textarea 
                name="description" 
                value={formData.description} 
                onChange={handleChange} 
                placeholder="Describe your property, amenities, and what makes it special..." 
                className="w-full p-3 border rounded-lg h-32 focus:ring-2 focus:ring-red-500" 
                required 
              />
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="block text-gray-700 font-semibold mb-2">Price per Night ($) *</label>
                <input 
                  name="price" 
                  type="number" 
                  value={formData.price} 
                  onChange={handleChange} 
                  placeholder="150" 
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
                  required 
                  min="1"
                />
              </div>
              
              <div>
                <label className="block text-gray-700 font-semibold mb-2">Location *</label>
                <input 
                  name="location" 
                  value={formData.location} 
                  onChange={handleChange} 
                  placeholder="City, Country" 
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
                  required 
                />
              </div>
              
              <div>
                <label className="block text-gray-700 font-semibold mb-2">Max Guests *</label>
                <input 
                  name="guests" 
                  type="number" 
                  value={formData.guests} 
                  onChange={handleChange} 
                  placeholder="4" 
                  className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
                  required 
                  min="1"
                />
              </div>
            </div>
          </div>

          {/* Images */}
          <div className="border-b pb-6">
            <h2 className="text-xl font-semibold mb-4">Property Images</h2>
            <div className="mb-4">
              <label className="block text-gray-700 font-semibold mb-2">Upload Images</label>
              <input 
                name="images" 
                type="file" 
                accept="image/*" 
                multiple 
                onChange={handleChange} 
                className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
              />
              {formData.images.length > 0 && (
                <p className="text-green-600 mt-2">✓ Selected: {formData.images.length} image(s)</p>
              )}
            </div>
            
            {formData.images.length > 0 && (
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-4">
                {formData.images.map((img, index) => (
                  <img 
                    key={index} 
                    src={img} 
                    alt={`Preview ${index + 1}`} 
                    className="w-full h-24 object-cover rounded-lg border" 
                  />
                ))}
              </div>
            )}
          </div>

          {/* Property Ratings/Features */}
          <div>
            <h2 className="text-xl font-semibold mb-4">Property Ratings (Optional)</h2>
            <p className="text-gray-600 mb-4">Rate different aspects of your property (0-5 scale)</p>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {Object.entries(formData.specificRatings).map(([key, value]) => (
                <div key={key}>
                  <label className="block text-gray-700 font-semibold mb-2 capitalize">
                    {key === 'checkin' ? 'Check-in Experience' : key} Rating
                  </label>
                  <input 
                    name={`specificRatings.${key}`} 
                    type="number" 
                    step="0.1" 
                    value={value} 
                    onChange={handleChange} 
                    placeholder="4.5" 
                    className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-red-500" 
                    min="0"
                    max="5"
                  />
                </div>
              ))}
            </div>
          </div>

          {/* Submit Button */}
          <div className="flex gap-4 pt-6">
            <button 
              type="submit" 
              className="flex-1 bg-red-500 text-white py-3 px-6 rounded-lg hover:bg-red-600 font-semibold disabled:opacity-50"
              disabled={loading}
            >
              {loading ? 'Creating Listing...' : 'Create Listing'}
            </button>
            
            <Link
              to="/host"
              className="flex-1 bg-gray-500 text-white py-3 px-6 rounded-lg hover:bg-gray-600 font-semibold text-center"
            >
              Cancel
            </Link>
          </div>
        </form>
        
        <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
          <h3 className="font-semibold text-yellow-800 mb-2">What happens next?</h3>
          <ul className="text-yellow-700 text-sm space-y-1">
            <li>• Your listing will be submitted for admin review</li>
            <li>• You'll be notified once it's approved or needs changes</li>
            <li>• Approved listings will be visible to guests</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default CreateListing;
