import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../utils/AuthContext';

const ListingReview = () => {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const [showRejectionModal, setShowRejectionModal] = useState(false);
  const { token } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchListing = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/${id}`, config);
        console.log('Listing review response:', res.data);
        setListing(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching listing:', err.response?.data || err.message);
        setError('Listing not found');
        setLoading(false);
      }
    };
    fetchListing();
  }, [id, token]);

  const handleStatusChange = async (newStatus) => {
    setActionLoading(true);
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const payload = { status: newStatus };
      
      // Add rejection reason if rejecting
      if (newStatus === 'rejected' && rejectionReason.trim()) {
        payload.rejectionReason = rejectionReason.trim();
      }
      
      await axios.patch(
        `${process.env.REACT_APP_API_URL}/api/listings/${id}/status`,
        payload,
        config
      );
      
      alert(`Listing ${newStatus} successfully!`);
      navigate('/admin/listings');
    } catch (err) {
      console.error('Error updating listing status:', err.response?.data || err.message);
      alert('Failed to update listing status');
    } finally {
      setActionLoading(false);
      setShowRejectionModal(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/600x400?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  const openRejectModal = () => {
    setShowRejectionModal(true);
    setRejectionReason('');
  };

  const closeRejectModal = () => {
    setShowRejectionModal(false);
    setRejectionReason('');
  };

  if (loading) return (
    <div className="container mx-auto px-4 py-8 text-center">
      <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500 mx-auto"></div>
      <p className="mt-4 text-gray-600">Loading listing details...</p>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8 text-center">
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <p className="text-red-600">{error}</p>
        <button 
          onClick={() => navigate('/admin/listings')}
          className="mt-4 bg-red-500 text-white px-6 py-2 rounded hover:bg-red-600"
        >
          Back to Listings
        </button>
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Review Listing</h1>
        <button 
          onClick={() => navigate('/admin/listings')}
          className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
        >
          Back to Review Queue
        </button>
      </div>

      {/* Status Badge */}
      <div className="mb-6">
        <span className="px-4 py-2 text-sm rounded-full font-medium bg-yellow-100 text-yellow-800">
          Status: Pending Review
        </span>
        <span className="ml-2 text-sm text-gray-600">
          Submitted: {new Date(listing.createdAt).toLocaleDateString()}
        </span>
      </div>

      {/* Main Content */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        
        {/* Listing Details - Left Column */}
        <div className="lg:col-span-2 space-y-6">
          
          {/* Images */}
          <div className="bg-white rounded-lg shadow-md overflow-hidden">
            <h3 className="text-xl font-semibold p-4 border-b">Property Images</h3>
            <div className="p-4">
              {listing.images && listing.images.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {listing.images.map((image, index) => (
                    <img
                      key={index}
                      src={getImageUrl(image)}
                      alt={`Property ${index + 1}`}
                      className="w-full h-48 object-cover rounded-lg"
                      onError={(e) => { 
                        e.target.src = 'https://via.placeholder.com/300x200?text=Image+Error'; 
                      }}
                    />
                  ))}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  No images provided
                </div>
              )}
            </div>
          </div>

          {/* Basic Information */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-xl font-semibold mb-4">Property Details</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 font-medium mb-1">Title</label>
                <p className="text-gray-900 bg-gray-50 p-2 rounded">{listing.title}</p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-1">Location</label>
                <p className="text-gray-900 bg-gray-50 p-2 rounded">{listing.location}</p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-1">Price per Night</label>
                <p className="text-gray-900 bg-gray-50 p-2 rounded font-bold text-green-600">${listing.price}</p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium mb-1">Maximum Guests</label>
                <p className="text-gray-900 bg-gray-50 p-2 rounded">{listing.guests}</p>
              </div>
            </div>
            
            <div className="mt-4">
              <label className="block text-gray-700 font-medium mb-1">Description</label>
              <div className="text-gray-900 bg-gray-50 p-3 rounded min-h-[100px]">
                {listing.description}
              </div>
            </div>
          </div>

          {/* Property Ratings */}
          {listing.specificRatings && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-xl font-semibold mb-4">Property Ratings</h3>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {Object.entries(listing.specificRatings).map(([key, value]) => (
                  <div key={key} className="text-center">
                    <div className="text-2xl font-bold text-blue-600">{value || 'N/A'}</div>
                    <div className="text-sm text-gray-600 capitalize">
                      {key === 'checkin' ? 'Check-in' : key}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Host Information & Actions - Right Column */}
        <div className="space-y-6">
          
          {/* Host Information */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-xl font-semibold mb-4">Host Information</h3>
            <div className="space-y-3">
              <div>
                <label className="block text-gray-700 font-medium">Name</label>
                <p className="text-gray-900">{listing.createdBy?.name || 'Unknown'}</p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium">Email</label>
                <p className="text-gray-900">{listing.createdBy?.email || 'N/A'}</p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium">Registration Date</label>
                <p className="text-gray-900">
                  {listing.createdBy?.createdAt 
                    ? new Date(listing.createdBy.createdAt).toLocaleDateString()
                    : 'N/A'
                  }
                </p>
              </div>
              <div>
                <label className="block text-gray-700 font-medium">Total Listings</label>
                <p className="text-gray-900">Coming soon</p>
              </div>
            </div>
          </div>

          {/* Review Actions */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-xl font-semibold mb-4">Review Actions</h3>
            
            <div className="space-y-4">
              {/* Approve Button */}
              <button
                onClick={() => handleStatusChange('approved')}
                disabled={actionLoading}
                className="w-full bg-green-500 text-white py-3 px-4 rounded-lg hover:bg-green-600 disabled:opacity-50 font-medium"
              >
                {actionLoading ? 'Processing...' : 'Approve Listing'}
              </button>
              
              {/* Reject Button */}
              <button
                onClick={openRejectModal}
                disabled={actionLoading}
                className="w-full bg-red-500 text-white py-3 px-4 rounded-lg hover:bg-red-600 disabled:opacity-50 font-medium"
              >
                Reject Listing
              </button>
            </div>

            <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
              <p className="text-sm text-yellow-800">
                <strong>Note:</strong> Once you approve or reject this listing, the host will be notified via email.
              </p>
            </div>
          </div>

          {/* Quick Stats */}
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-semibold mb-2">Quick Review Checklist</h4>
            <div className="space-y-2 text-sm">
              <div className="flex items-center">
                <span className="text-green-500 mr-2">✓</span>
                <span>Images provided: {listing.images?.length || 0}</span>
              </div>
              <div className="flex items-center">
                <span className="text-green-500 mr-2">✓</span>
                <span>Description complete</span>
              </div>
              <div className="flex items-center">
                <span className="text-green-500 mr-2">✓</span>
                <span>Pricing set</span>
              </div>
              <div className="flex items-center">
                <span className="text-green-500 mr-2">✓</span>
                <span>Location specified</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Rejection Modal */}
      {showRejectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
            <h3 className="text-xl font-semibold mb-4">Reject Listing</h3>
            <p className="text-gray-600 mb-4">
              Please provide a reason for rejecting this listing (optional):
            </p>
            <textarea
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              placeholder="e.g., Images are unclear, description needs more detail, pricing seems unrealistic..."
              className="w-full p-3 border rounded-lg h-24 resize-none"
            />
            <div className="flex gap-3 mt-4">
              <button
                onClick={() => handleStatusChange('rejected')}
                disabled={actionLoading}
                className="flex-1 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 disabled:opacity-50"
              >
                {actionLoading ? 'Rejecting...' : 'Confirm Reject'}
              </button>
              <button
                onClick={closeRejectModal}
                disabled={actionLoading}
                className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListingReview;
