import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const ListingReview = () => {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const [showRejectionModal, setShowRejectionModal] = useState(false);
  const { token } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchListing = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/${id}?status=pending`, config);
        setListing(res.data);
        setLoading(false);
      } catch (err) {
        setError(`Failed to fetch listing: ${err.response?.data?.message || err.message}`);
        setLoading(false);
      }
    };
    if (token) {
      fetchListing();
    } else {
      setError('Authentication required');
      setLoading(false);
    }
  }, [id, token]);

  const handleStatusChange = async (newStatus) => {
    setActionLoading(true);
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      };
      
      const payload = {
        status: newStatus,
        ...(newStatus === 'rejected' && rejectionReason.trim() && { rejectionReason: rejectionReason.trim() })
      };
      
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/listings/${id}`,
        payload,
        config
      );
      
      // Refresh the listing to reflect the new status
      const updatedRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/${id}`, config);
      setListing(updatedRes.data);
      navigate('/admin/listings');
    } catch (err) {
      setError(`Failed to update listing: ${err.response?.data?.message || err.message}`);
    } finally {
      setActionLoading(false);
      setShowRejectionModal(false);
    }
  };

  const closeRejectModal = () => {
    setShowRejectionModal(false);
    setRejectionReason('');
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;
  if (!listing) return <div className="container mx-auto px-4 py-8 text-center">Listing not found</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Review Listing: {listing.title}</h1>
        <Link to="/admin/listings" className="text-blue-500 hover:underline">
          Back to Listings
        </Link>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="md:col-span-2">
          <div className="mb-6">
            <h3 className="text-xl font-semibold mb-2">Listing Details</h3>
            <p><strong>Location:</strong> {listing.location}</p>
            <p><strong>Price:</strong> ${listing.price}/night</p>
            <p><strong>Guests:</strong> {listing.guests}</p>
            <p><strong>Status:</strong> {listing.status}</p>
            <p className="mt-2">{listing.description}</p>
          </div>

          <div className="mb-6">
            <h3 className="text-xl font-semibold mb-2">Images</h3>
            <div className="grid grid-cols-2 gap-4">
              {listing.images?.length > 0 ? (
                listing.images.map((img, index) => (
                  <img key={index} src={getImageUrl(img)} alt={`Property ${index + 1}`} className="w-full h-48 object-cover rounded-lg" />
                ))
              ) : (
                <p>No images available</p>
              )}
            </div>
          </div>

          <div className="flex gap-4">
            <button
              onClick={() => handleStatusChange('approved')}
              disabled={actionLoading || listing.status !== 'pending'}
              className="bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 disabled:opacity-50"
            >
              {actionLoading ? 'Approving...' : 'Approve'}
            </button>
            <button
              onClick={() => setShowRejectionModal(true)}
              disabled={actionLoading || listing.status !== 'pending'}
              className="bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 disabled:opacity-50"
            >
              {actionLoading ? 'Rejecting...' : 'Reject'}
            </button>
          </div>

          <div className="mt-6 bg-gray-50 rounded-lg p-4">
            <h4 className="font-semibold mb-2">Approval Guidelines</h4>
            <p className="text-sm text-gray-600">
              Approving this listing will make it visible to all users. If you reject
              this listing, the host will be notified via email with the provided reason.
            </p>
          </div>
        </div>

        <div className="bg-gray-50 rounded-lg p-4">
          <h4 className="font-semibold mb-2">Quick Review Checklist</h4>
          <div className="space-y-2 text-sm">
            <div className="flex items-center">
              <span className={listing.images?.length > 0 ? "text-green-500 mr-2" : "text-red-500 mr-2"}>✓</span>
              <span>Images provided: {listing.images?.length || 0}</span>
            </div>
            <div className="flex items-center">
              <span className={listing.description ? "text-green-500 mr-2" : "text-red-500 mr-2"}>✓</span>
              <span>Description: {listing.description ? 'Complete' : 'Missing'}</span>
            </div>
            <div className="flex items-center">
              <span className={listing.price > 0 ? "text-green-500 mr-2" : "text-red-500 mr-2"}>✓</span>
              <span>Pricing: {listing.price > 0 ? 'Set' : 'Not set'}</span>
            </div>
            <div className="flex items-center">
              <span className={listing.location ? "text-green-500 mr-2" : "text-red-500 mr-2"}>✓</span>
              <span>Location: {listing.location ? 'Specified' : 'Missing'}</span>
            </div>
            <div className="flex items-center">
              <span className={listing.guests > 0 ? "text-green-500 mr-2" : "text-red-500 mr-2"}>✓</span>
              <span>Guests: {listing.guests > 0 ? 'Set' : 'Not set'}</span>
            </div>
          </div>
        </div>
      </div>

      {showRejectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
            <h3 className="text-xl font-semibold mb-4">Reject Listing</h3>
            <p className="text-gray-600 mb-4">
              Please provide a reason for rejecting this listing:
            </p>
            <textarea
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              placeholder="e.g., Images are unclear, description needs more detail, pricing seems unrealistic..."
              className="w-full p-3 border rounded-lg h-24 resize-none"
              required
            />
            <div className="flex gap-3 mt-4">
              <button
                onClick={() => handleStatusChange('rejected')}
                disabled={actionLoading || !rejectionReason.trim()}
                className="flex-1 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 disabled:opacity-50"
              >
                {actionLoading ? 'Rejecting...' : 'Confirm Reject'}
              </button>
              <button
                onClick={closeRejectModal}
                disabled={actionLoading}
                className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListingReview;
