import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const ViewListings = () => {
  const [listings, setListings] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('pending'); // pending, approved, rejected, all
  const { token } = useAuth();

  useEffect(() => {
    const fetchListings = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings`, config);
        console.log('All listings response:', res.data);
        setListings(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching listings:', err.response?.data || err.message);
        setError('Failed to fetch listings');
        setLoading(false);
      }
    };
    fetchListings();
  }, [token]);

  const handleStatusChange = async (listingId, newStatus) => {
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      await axios.patch(
        `${process.env.REACT_APP_API_URL}/api/listings/${listingId}/status`,
        { status: newStatus },
        config
      );

      // Update the listing status in the local state
      setListings(listings.map(listing => 
        listing._id === listingId 
          ? { ...listing, status: newStatus }
          : listing
      ));

      alert(`Listing ${newStatus} successfully!`);
    } catch (err) {
      console.error('Error updating listing status:', err.response?.data || err.message);
      alert('Failed to update listing status');
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredListings = listings.filter(listing => {
    if (filter === 'all') return true;
    return listing.status === filter;
  });

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Manage Listings</h1>
        <div className="text-sm text-gray-600">
          Total: {listings.length} listings
        </div>
      </div>

      {/* Filter Buttons */}
      <div className="mb-6">
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setFilter('pending')}
            className={`px-4 py-2 rounded ${
              filter === 'pending' 
                ? 'bg-yellow-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Pending ({listings.filter(l => l.status === 'pending').length})
          </button>
          <button
            onClick={() => setFilter('approved')}
            className={`px-4 py-2 rounded ${
              filter === 'approved' 
                ? 'bg-green-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Approved ({listings.filter(l => l.status === 'approved').length})
          </button>
          <button
            onClick={() => setFilter('rejected')}
            className={`px-4 py-2 rounded ${
              filter === 'rejected' 
                ? 'bg-red-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Rejected ({listings.filter(l => l.status === 'rejected').length})
          </button>
          <button
            onClick={() => setFilter('all')}
            className={`px-4 py-2 rounded ${
              filter === 'all' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            All ({listings.length})
          </button>
        </div>
      </div>

      {/* Listings Grid */}
      {filteredListings.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">
            No {filter === 'all' ? '' : filter} listings found
          </h3>
          <p className="text-gray-600">
            {filter === 'pending' 
              ? "There are no listings waiting for approval."
              : `There are no ${filter} listings at the moment.`
            }
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg overflow-hidden shadow-lg bg-white">
              {/* Status Badge */}
              <div className="relative">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={getImageUrl(listing.images[0])}
                    alt={listing.title}
                    className="w-full h-48 object-cover"
                    onError={(e) => { 
                      e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                    }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <span className={`absolute top-2 right-2 px-3 py-1 text-sm rounded-full font-medium ${getStatusColor(listing.status || 'pending')}`}>
                  {(listing.status || 'pending').charAt(0).toUpperCase() + (listing.status || 'pending').slice(1)}
                </span>
              </div>

              {/* Listing Details */}
              <div className="p-4">
                <h2 className="text-xl font-semibold mb-2 truncate">{listing.title}</h2>
                <p className="text-gray-600 mb-2">{listing.location}</p>
                <p className="text-lg font-bold text-red-600 mb-2">${listing.price}/night</p>
                <p className="text-sm text-gray-700 mb-2">Max guests: {listing.guests}</p>

                {/* Host Information */}
                <div className="text-sm text-gray-600 mb-4">
                  <p><strong>Host:</strong> {listing.createdBy?.name || 'Unknown'}</p>
                  <p><strong>Email:</strong> {listing.createdBy?.email || 'N/A'}</p>
                  <p><strong>Created:</strong> {new Date(listing.createdAt).toLocaleDateString()}</p>
                </div>

                {/* Description Preview */}
                <p className="text-sm text-gray-700 mb-4 line-clamp-3">
                  {listing.description?.length > 100 
                    ? `${listing.description.substring(0, 100)}...` 
                    : listing.description
                  }
                </p>

                {/* Action Buttons */}
                <div className="space-y-2">
                  {listing.status === 'pending' && (
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleStatusChange(listing._id, 'approved')}
                        className="flex-1 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 text-sm font-medium"
                      >
                        Approve
                      </button>
                      <button
                        onClick={() => handleStatusChange(listing._id, 'rejected')}
                        className="flex-1 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 text-sm font-medium"
                      >
                        Reject
                      </button>
                    </div>
                  )}

                  {listing.status === 'approved' && (
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleStatusChange(listing._id, 'rejected')}
                        className="flex-1 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 text-sm font-medium"
                      >
                        Reject
                      </button>
                    </div>
                  )}

                  {listing.status === 'rejected' && (
                    <div className="flex gap-2">
                      <button
                        onClick={() => handleStatusChange(listing._id, 'approved')}
                        className="flex-1 bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 text-sm font-medium"
                      >
                        Approve
                      </button>
                    </div>
                  )}

                  <a 
                    href={`/locations/${listing._id}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block w-full bg-gray-500 text-white py-2 px-4 rounded hover:bg-gray-600 text-sm font-medium text-center"
                  >
                    View Details
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary Stats */}
      {listings.length > 0 && (
        <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-yellow-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-yellow-800">Pending</h4>
            <p className="text-2xl font-bold text-yellow-600">
              {listings.filter(l => l.status === 'pending').length}
            </p>
          </div>
          <div className="bg-green-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-green-800">Approved</h4>
            <p className="text-2xl font-bold text-green-600">
              {listings.filter(l => l.status === 'approved').length}
            </p>
          </div>
          <div className="bg-red-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-red-800">Rejected</h4>
            <p className="text-2xl font-bold text-red-600">
              {listings.filter(l => l.status === 'rejected').length}
            </p>
          </div>
          <div className="bg-gray-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-gray-800">Total</h4>
            <p className="text-2xl font-bold text-gray-600">{listings.length}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default ViewListings;
