import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const HostDashboard = () => {
  const [stats, setStats] = useState({
    totalListings: 0,
    pendingListings: 0,
    approvedListings: 0,
    totalReservations: 0,
    monthlyRevenue: 0
  });
  const [recentReservations, setRecentReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, token } = useAuth();

  useEffect(() => {
    const fetchHostData = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        
        // Fetch host's listings
        const listingsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings`, config);
        const hostListings = listingsRes.data.filter(listing => listing.createdBy?._id === user?.id);
        
        // Fetch host's reservations
        const reservationsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/reservations/host/all`, config);
        
        // Calculate stats
        const totalListings = hostListings.length;
        const pendingListings = hostListings.filter(l => l.status === 'pending').length;
        const approvedListings = hostListings.filter(l => l.status === 'approved').length;
        const totalReservations = reservationsRes.data.length;
        
        // Calculate monthly revenue (current month)
        const currentMonth = new Date().getMonth();
        const currentYear = new Date().getFullYear();
        const monthlyRevenue = reservationsRes.data
          .filter(r => {
            const reservationDate = new Date(r.createdAt);
            return reservationDate.getMonth() === currentMonth && 
                   reservationDate.getFullYear() === currentYear;
          })
          .reduce((sum, r) => sum + r.totalCost, 0);

        setStats({
          totalListings,
          pendingListings,
          approvedListings,
          totalReservations,
          monthlyRevenue
        });

        setRecentReservations(reservationsRes.data.slice(0, 5));
        setLoading(false);
      } catch (err) {
        console.error('Error fetching host data:', err);
        setLoading(false);
      }
    };

    if (user && token) {
      fetchHostData();
    }
  }, [user, token]);

  if (loading) return <div className="container mx-auto px-4 py-8">Loading...</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Host Dashboard</h1>
      <p className="text-gray-600 mb-8">Welcome back, {user?.name}! Manage your properties and track your hosting performance.</p>
      
      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-6 mb-8">
        <div className="bg-blue-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-blue-800">Total Properties</h3>
          <p className="text-3xl font-bold text-blue-600">{stats.totalListings}</p>
        </div>
        <div className="bg-yellow-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-yellow-800">Pending Approval</h3>
          <p className="text-3xl font-bold text-yellow-600">{stats.pendingListings}</p>
        </div>
        <div className="bg-green-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-green-800">Approved</h3>
          <p className="text-3xl font-bold text-green-600">{stats.approvedListings}</p>
        </div>
        <div className="bg-purple-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-purple-800">Total Bookings</h3>
          <p className="text-3xl font-bold text-purple-600">{stats.totalReservations}</p>
        </div>
        <div className="bg-red-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-red-800">Monthly Revenue</h3>
          <p className="text-3xl font-bold text-red-600">${stats.monthlyRevenue}</p>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <Link 
          to="/host/create-listing" 
          className="bg-green-500 text-white p-6 rounded-lg shadow hover:bg-green-600 text-center block"
        >
          <h3 className="text-xl font-semibold mb-2">Add New Property</h3>
          <p className="text-lg">List a new property</p>
        </Link>
        
        <Link 
          to="/host/reservations" 
          className="bg-blue-500 text-white p-6 rounded-lg shadow hover:bg-blue-600 text-center block"
        >
          <h3 className="text-xl font-semibold mb-2">View Reservations</h3>
          <p className="text-lg">Manage your bookings</p>
        </Link>
        
        <div className="bg-gray-500 text-white p-6 rounded-lg shadow text-center">
          <h3 className="text-xl font-semibold mb-2">Host Resources</h3>
          <p className="text-lg">Tips and guides</p>
        </div>
      </div>

      {/* Recent Reservations */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-bold">Recent Reservations</h2>
          <Link 
            to="/host/reservations" 
            className="text-blue-500 hover:text-blue-600 font-medium"
          >
            View all →
          </Link>
        </div>
        
        {recentReservations.length === 0 ? (
          <div className="text-center py-8 bg-gray-50 rounded-lg">
            <h3 className="text-xl font-semibold mb-2">No reservations yet</h3>
            <p className="text-gray-600 mb-4">Once guests book your properties, they'll appear here.</p>
          </div>
        ) : (
          <div className="space-y-4">
            {recentReservations.map((reservation) => (
              <div key={reservation._id} className="border rounded-lg p-4 shadow bg-white">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-semibold">
                      {reservation.listingId?.title || 'Property'}
                    </h3>
                    <p className="text-gray-600">Guest: {reservation.createdBy?.name || 'Guest'}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(reservation.checkIn).toLocaleDateString()} - {new Date(reservation.checkOut).toLocaleDateString()}
                    </p>
                    <p className="text-sm text-gray-500">{reservation.guests} guests</p>
                  </div>
                  <div className="text-right">
                    <p className="text-xl font-bold text-green-600">${reservation.totalCost}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(reservation.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Getting Started Guide */}
      {stats.totalListings === 0 && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
          <h3 className="text-xl font-semibold mb-4">Getting Started as a Host</h3>
          <div className="space-y-3">
            <div className="flex items-center">
              <span className="bg-blue-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm mr-3">1</span>
              <span>Create your first listing</span>
            </div>
            <div className="flex items-center">
              <span className="bg-gray-400 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm mr-3">2</span>
              <span>Wait for admin approval</span>
            </div>
            <div className="flex items-center">
              <span className="bg-gray-400 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm mr-3">3</span>
              <span>Start receiving bookings</span>
            </div>
          </div>
          <Link 
            to="/host/create-listing"
            className="inline-block mt-4 bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
          >
            Create Your First Listing
          </Link>
        </div>
      )}
    </div>
  );
};

export default HostDashboard;
