import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';
import { Link } from 'react-router-dom';

const HostReservations = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all'); // all, upcoming, current, past
  const { token, user } = useAuth();

  useEffect(() => {
    const fetchHostReservations = async () => {
      if (!token || !user) {
        setError('Please login to view reservations');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        console.log('Fetching host reservations for user:', user.id);
        
        const config = {
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        };
        
        // Use the new host reservations endpoint
        const reservationsRes = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/reservations/host/all`, 
          config
        );
        
        console.log('Host reservations response:', reservationsRes.data);
        setReservations(reservationsRes.data);
        setError(null);
        
      } catch (err) {
        console.error('Error fetching host reservations:', err.response?.data || err.message);
        
        if (err.response?.status === 401) {
          setError('Authentication failed. Please login again.');
        } else if (err.response?.status === 403) {
          setError('You must be a host to view this page.');
        } else {
          setError(err.response?.data?.message || 'Failed to load reservations');
        }
      } finally {
        setLoading(false);
      }
    };

    if (user && token) {
      fetchHostReservations();
    }
  }, [token, user]);

  // Helper function to get image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/150x100?text=Property+Image';
    if (imagePath.startsWith('http')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  // Helper function to get reservation status
  const getReservationStatus = (reservation) => {
    const now = new Date();
    const checkIn = new Date(reservation.checkIn);
    const checkOut = new Date(reservation.checkOut);

    if (reservation.status === 'cancelled') return 'cancelled';
    if (reservation.status === 'completed') return 'completed';
    if (checkIn <= now && checkOut >= now) return 'current';
    if (checkIn > now) return 'upcoming';
    return 'past';
  };

  const filterReservations = (reservations, filter) => {
    switch (filter) {
      case 'upcoming':
        return reservations.filter(r => getReservationStatus(r) === 'upcoming');
      case 'current':
        return reservations.filter(r => getReservationStatus(r) === 'current');
      case 'past':
        return reservations.filter(r => ['past', 'completed'].includes(getReservationStatus(r)));
      case 'cancelled':
        return reservations.filter(r => getReservationStatus(r) === 'cancelled');
      default:
        return reservations;
    }
  };

  const filteredReservations = filterReservations(reservations, filter);

  const getStatusColor = (reservation) => {
    const status = getReservationStatus(reservation);
    switch (status) {
      case 'current': return 'bg-green-100 text-green-800';
      case 'upcoming': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      case 'past':
      case 'completed': return 'bg-gray-100 text-gray-800';
      default: return 'bg-yellow-100 text-yellow-800';
    }
  };

  const getStatusText = (reservation) => {
    const status = getReservationStatus(reservation);
    switch (status) {
      case 'current': return 'Active';
      case 'upcoming': return 'Upcoming';
      case 'cancelled': return 'Cancelled';
      case 'past':
      case 'completed': return 'Completed';
      default: return 'Pending';
    }
  };

  if (loading) return (
    <div className="container mx-auto px-4 py-8 text-center">
      <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-red-500 mx-auto"></div>
      <p className="mt-4 text-gray-600">Loading reservations...</p>
    </div>
  );

  if (error) return (
    <div className="container mx-auto px-4 py-8 text-center">
      <div className="bg-red-50 border border-red-200 rounded-lg p-6">
        <p className="text-red-600">{error}</p>
        <Link 
          to="/login"
          className="mt-4 inline-block bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600"
        >
          Go to Login
        </Link>
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Property Reservations</h1>
        <Link 
          to="/host" 
          className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
        >
          Back to Dashboard
        </Link>
      </div>

      {/* Filter Buttons */}
      <div className="mb-6">
        <div className="flex flex-wrap gap-2">
          <button
            onClick={() => setFilter('all')}
            className={`px-4 py-2 rounded ${
              filter === 'all' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            All ({reservations.length})
          </button>
          <button
            onClick={() => setFilter('current')}
            className={`px-4 py-2 rounded ${
              filter === 'current' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Current ({filterReservations(reservations, 'current').length})
          </button>
          <button
            onClick={() => setFilter('upcoming')}
            className={`px-4 py-2 rounded ${
              filter === 'upcoming' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Upcoming ({filterReservations(reservations, 'upcoming').length})
          </button>
          <button
            onClick={() => setFilter('past')}
            className={`px-4 py-2 rounded ${
              filter === 'past' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Past ({filterReservations(reservations, 'past').length})
          </button>
          <button
            onClick={() => setFilter('cancelled')}
            className={`px-4 py-2 rounded ${
              filter === 'cancelled' 
                ? 'bg-black text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            Cancelled ({filterReservations(reservations, 'cancelled').length})
          </button>
        </div>
      </div>

      {/* Reservations List */}
      {filteredReservations.length === 0 ? (
        <div className="text-center py-12 bg-gray-50 rounded-lg">
          <h3 className="text-xl font-semibold mb-2">
            No {filter === 'all' ? '' : filter} reservations
          </h3>
          <p className="text-gray-600 mb-6">
            {filter === 'all' 
              ? "You don't have any reservations yet. Once guests book your properties, they'll appear here."
              : `You don't have any ${filter} reservations.`
            }
          </p>
          {reservations.length === 0 && (
            <Link 
              to="/host/listings"
              className="bg-red-500 text-white px-6 py-3 rounded-lg hover:bg-red-600"
            >
              View Your Properties
            </Link>
          )}
        </div>
      ) : (
        <div className="space-y-6">
          {filteredReservations.map((reservation) => (
            <div key={reservation._id} className="border rounded-lg p-6 shadow-lg bg-white">
              <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
                
                {/* Property Image */}
                <div className="lg:col-span-1">
                  {reservation.listingId?.images?.[0] ? (
                    <img
                      src={getImageUrl(reservation.listingId.images[0])}
                      alt={reservation.listingId.title}
                      className="w-full h-32 object-cover rounded-lg"
                      onError={(e) => { 
                        e.target.src = 'https://via.placeholder.com/150x100?text=Property+Image';
                      }}
                    />
                  ) : (
                    <div className="w-full h-32 bg-gray-200 flex items-center justify-center rounded-lg">
                      <span className="text-gray-500">No Image</span>
                    </div>
                  )}
                </div>

                {/* Property & Guest Details */}
                <div className="lg:col-span-2">
                  <div className="flex justify-between items-start mb-3">
                    <div>
                      <h3 className="text-xl font-semibold">
                        {reservation.listingId?.title || 'Property'}
                      </h3>
                      <p className="text-gray-600">
                        {reservation.listingId?.location || 'Location not available'}
                      </p>
                    </div>
                    <span className={`px-3 py-1 text-sm rounded-full ${getStatusColor(reservation)}`}>
                      {getStatusText(reservation)}
                    </span>
                  </div>
                  
                  <div className="space-y-2 text-sm">
                    <p><strong>Reservation ID:</strong> #{reservation._id.slice(-8)}</p>
                    <p><strong>Guest:</strong> {reservation.guestName || reservation.createdBy?.name || 'Guest'}</p>
                    <p><strong>Contact:</strong> {reservation.createdBy?.email || 'Not available'}</p>
                    <p><strong>Guests:</strong> {reservation.guests}</p>
                    <p><strong>Booked:</strong> {new Date(reservation.createdAt).toLocaleDateString()}</p>
                    {reservation.specialRequests && (
                      <p><strong>Special Requests:</strong> {reservation.specialRequests}</p>
                    )}
                  </div>
                </div>

                {/* Dates & Payment */}
                <div className="lg:col-span-1">
                  <div className="space-y-3">
                    <div>
                      <p className="text-sm text-gray-600">Check-in</p>
                      <p className="font-semibold">{new Date(reservation.checkIn).toLocaleDateString()}</p>
                    </div>
                    <div>
                      <p className="text-sm text-gray-600">Check-out</p>
                      <p className="font-semibold">{new Date(reservation.checkOut).toLocaleDateString()}</p>
                    </div>
                    <div>
                      <p className="text-sm text-gray-600">Total Payment</p>
                      <p className="text-2xl font-bold text-green-600">${reservation.totalCost}</p>
                    </div>
                    <div>
                      <p className="text-sm text-gray-600">Nights</p>
                      <p className="font-semibold">
                        {Math.ceil((new Date(reservation.checkOut) - new Date(reservation.checkIn)) / (1000 * 60 * 60 * 24))}
                      </p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="mt-4 pt-4 border-t border-gray-200">
                <div className="flex gap-3">
                  <Link 
                    to={`/locations/${reservation.listingId?._id}`}
                    className="bg-gray-500 text-white px-4 py-2 rounded text-sm hover:bg-gray-600"
                  >
                    View Property
                  </Link>
                  
                  {getReservationStatus(reservation) === 'current' && (
                    <div className="bg-green-500 text-white px-4 py-2 rounded text-sm">
                      Currently Hosting
                    </div>
                  )}
                  
                  {getReservationStatus(reservation) === 'upcoming' && (
                    <div className="bg-blue-500 text-white px-4 py-2 rounded text-sm">
                      Guest Arriving Soon
                    </div>
                  )}

                  {getReservationStatus(reservation) === 'past' && (
                    <div className="bg-gray-400 text-white px-4 py-2 rounded text-sm">
                      Stay Completed
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Summary Stats */}
      {reservations.length > 0 && (
        <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-black text-white p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold">Total Reservations</h4>
            <p className="text-2xl font-bold">{reservations.length}</p>
          </div>
          <div className="bg-green-600 text-white p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold">Active Guests</h4>
            <p className="text-2xl font-bold">{filterReservations(reservations, 'current').length}</p>
          </div>
          <div className="bg-blue-600 text-white p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold">Upcoming Bookings</h4>
            <p className="text-2xl font-bold">{filterReservations(reservations, 'upcoming').length}</p>
          </div>
          <div className="bg-purple-600 text-white p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold">Total Revenue</h4>
            <p className="text-2xl font-bold">
              ${reservations
                .filter(r => getReservationStatus(r) !== 'cancelled')
                .reduce((sum, r) => sum + r.totalCost, 0)
                .toLocaleString()
              }
            </p>
          </div>
        </div>
      )}

      {/* Quick Actions */}
      <div className="mt-8 bg-gray-50 rounded-lg p-6">
        <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>
        <div className="flex flex-wrap gap-4">
          <Link 
            to="/host/listings"
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Manage Properties
          </Link>
          <Link 
            to="/host/create-listing"
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Add New Property
          </Link>
          <Link 
            to="/host"
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            Dashboard
          </Link>
        </div>
      </div>
    </div>
  );
};

export default HostReservations;
