import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useAuth } from '../../utils/AuthContext';

const ViewListings = () => {
  const [listings, setListings] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all'); // pending, approved, rejected, all
  const { token } = useAuth();

  // Helper function to get image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  useEffect(() => {
    const fetchHostListings = async () => {
      if (!token) {
        setError('No authentication token found. Please log in.');
        setLoading(false);
        return;
      }

      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/host/listings`, config);
        console.log('Host listings response:', res.data);
        setListings(res.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching host listings:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch your listings. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    fetchHostListings();
  }, [token]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'approved': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredListings = listings.filter(listing => {
    if (filter === 'all') return true;
    return (listing.status || 'pending') === filter;
  });

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading your listings...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center text-red-500">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Your Listings</h1>
        <Link 
          to="/host/create-listing"
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Create New Listing
        </Link>
      </div>

      <div className="mb-4">
        <label htmlFor="filter" className="mr-2">Filter by:</label>
        <select 
          id="filter" 
          value={filter} 
          onChange={(e) => setFilter(e.target.value)} 
          className="p-2 border rounded"
        >
          <option value="all">All</option>
          <option value="pending">Pending</option>
          <option value="approved">Approved</option>
          <option value="rejected">Rejected</option>
        </select>
      </div>

      {filteredListings.length === 0 ? (
        <p className="text-gray-600 text-center">No listings found.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {filteredListings.map((listing) => (
            <div key={listing._id} className="border rounded-lg overflow-hidden shadow">
              <div className="relative">
                {listing.images && listing.images.length > 0 ? (
                  <img
                    src={getImageUrl(listing.images[0])}
                    alt={listing.title}
                    className="w-full h-48 object-cover"
                    onError={(e) => { 
                      e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                    }}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500">No Image</span>
                  </div>
                )}
                <span className={`absolute top-2 right-2 px-2 py-1 text-xs rounded-full ${getStatusColor(listing.status)}`}>
                  {listing.status || 'pending'}
                </span>
              </div>
              <div className="p-4">
                <h3 className="text-lg font-semibold truncate">{listing.title}</h3>
                <p className="text-gray-600 text-sm">{listing.location}</p>
                <p className="text-lg font-bold text-red-600 mt-2">${listing.price}/night</p>
                <div className="flex gap-2 mt-4">
                  <Link 
                    to={`/host/listings/${listing._id}/edit`}
                    className="flex-1 bg-blue-500 text-white py-2 px-3 rounded text-sm text-center hover:bg-blue-600"
                  >
                    Edit
                  </Link>
                  <Link 
                    to={`/locations/${listing._id}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex-1 bg-gray-500 text-white py-2 px-3 rounded text-sm text-center hover:bg-gray-600"
                  >
                    View
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {listings.length > 0 && (
        <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-gray-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-gray-800">Total</h4>
            <p className="text-2xl font-bold text-gray-600">{listings.length}</p>
          </div>
          <div className="bg-yellow-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-yellow-800">Pending</h4>
            <p className="text-2xl font-bold text-yellow-600">
              {listings.filter(l => (l.status || 'pending') === 'pending').length}
            </p>
          </div>
          <div className="bg-green-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-green-800">Approved</h4>
            <p className="text-2xl font-bold text-green-600">
              {listings.filter(l => l.status === 'approved').length}
            </p>
          </div>
          <div className="bg-red-100 p-4 rounded-lg text-center">
            <h4 className="text-lg font-semibold text-red-800">Rejected</h4>
            <p className="text-2xl font-bold text-red-600">
              {listings.filter(l => l.status === 'rejected').length}
            </p>
          </div>
        </div>
      )}

      <div className="mt-8 text-center">
        <Link 
          to="/host"
          className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
        >
          Back to Host Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ViewListings;
