import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const HostDashboard = () => {
  const [stats, setStats] = useState({
    totalListings: 0,
    approvedListings: 0,
    pendingListings: 0,
    totalReservations: 0,
    totalRevenue: 0
  });
  const [recentListings, setRecentListings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { token } = useAuth();

  // Helper function to get image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/300x200?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  useEffect(() => {
    const fetchHostData = async () => {
      if (!token) {
        setError('No authentication token found. Please log in.');
        setLoading(false);
        return;
      }

      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        
        // Fetch host's listings
        const listingsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/host/listings`, config);
        const listings = listingsRes.data;
        
        // Calculate stats
        const stats = {
          totalListings: listings.length,
          approvedListings: listings.filter(l => l.status === 'approved').length,
          pendingListings: listings.filter(l => l.status === 'pending').length,
          totalReservations: 0,
          totalRevenue: 0
        };
        
        // Fetch host reservations
        try {
          const reservationsRes = await axios.get(`${process.env.REACT_APP_API_URL}/api/reservations/host/all`, config);
          const hostReservations = reservationsRes.data;
          
          stats.totalReservations = hostReservations.length;
          stats.totalRevenue = hostReservations.reduce((sum, r) => sum + (r.totalCost || 0), 0);
        } catch (reservationError) {
          console.warn('Could not fetch host reservations:', reservationError.message);
        }
        
        setStats(stats);
        setRecentListings(listings.slice(0, 3));
        setError(null);
      } catch (err) {
        console.error('Error fetching host data:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Failed to fetch host data. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchHostData();
  }, [token]);

  if (loading) return <div className="container mx-auto px-4 py-8">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-red-500">{error}</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Host Dashboard</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-blue-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-blue-800">Total Listings</h3>
          <p className="text-3xl font-bold text-blue-600">{stats.totalListings}</p>
        </div>
        <div className="bg-green-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-green-800">Approved Listings</h3>
          <p className="text-3xl font-bold text-green-600">{stats.approvedListings}</p>
        </div>
        <div className="bg-yellow-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-yellow-800">Pending Listings</h3>
          <p className="text-3xl font-bold text-yellow-600">{stats.pendingListings}</p>
        </div>
        <div className="bg-purple-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-purple-800">Total Reservations</h3>
          <p className="text-3xl font-bold text-purple-600">{stats.totalReservations}</p>
        </div>
        <div className="bg-red-100 p-6 rounded-lg">
          <h3 className="text-xl font-semibold text-red-800">Total Revenue</h3>
          <p className="text-3xl font-bold text-red-600">${stats.totalRevenue.toLocaleString()}</p>
        </div>
      </div>

      {recentListings.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-4">Recent Listings</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {recentListings.map((listing) => (
              <div key={listing._id} className="border rounded-lg overflow-hidden shadow">
                <div className="relative">
                  {listing.images && listing.images.length > 0 ? (
                    <img
                      src={getImageUrl(listing.images[0])}
                      alt={listing.title}
                      className="w-full h-48 object-cover"
                      onError={(e) => { 
                        e.target.src = 'https://via.placeholder.com/300x200?text=Property+Image'; 
                      }}
                    />
                  ) : (
                    <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                      <span className="text-gray-500">No Image</span>
                    </div>
                  )}
                  <span className={`absolute top-2 right-2 px-2 py-1 text-xs rounded-full ${listing.status === 'approved' ? 'bg-green-100 text-green-800' : listing.status === 'pending' ? 'bg-yellow-100 text-yellow-800' : 'bg-red-100 text-red-800'}`}>
                    {listing.status || 'pending'}
                  </span>
                </div>
                <div className="p-4">
                  <h3 className="text-lg font-semibold truncate">{listing.title}</h3>
                  <p className="text-gray-600 text-sm">{listing.location}</p>
                  <p className="text-lg font-bold text-red-600 mt-2">${listing.price}/night</p>
                  <div className="flex gap-2 mt-3">
                    <Link 
                      to={`/host/listings/${listing._id}/edit`}
                      className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600"
                    >
                      Edit
                    </Link>
                    <Link 
                      to={`/locations/${listing._id}`}
                      className="bg-gray-500 text-white px-3 py-1 rounded text-sm hover:bg-gray-600"
                    >
                      View
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="mt-8 text-center">
        <Link 
          to="/host/listings"
          className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
        >
          View All Listings
        </Link>
      </div>
    </div>
  );
};

export default HostDashboard;
