import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../../utils/AuthContext';

const ListingReview = () => {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const [showRejectionModal, setShowRejectionModal] = useState(false);
  const { token } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchListing = async () => {
      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/listings/${id}`, config);
        console.log('Listing review response:', res.data);
        setListing(res.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching listing:', {
          message: err.message,
          response: err.response?.data,
          status: err.response?.status,
        });
        setError(`Failed to fetch listing: ${err.response?.data?.message || err.message}`);
        setLoading(false);
      }
    };
    if (token) {
      fetchListing();
    } else {
      setError('Authentication required');
      setLoading(false);
    }
  }, [id, token]);

  const handleStatusChange = async (newStatus) => {
    setActionLoading(true);
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      };
      
      // Include all required fields from the original listing
      const payload = {
        title: listing.title,
        description: listing.description,
        price: listing.price,
        location: listing.location,
        guests: listing.guests,
        images: listing.images || [],
        specificRatings: listing.specificRatings || {
          cleanliness: 0,
          communication: 0,
          checkin: 0,
          accuracy: 0,
          location: 0,
          value: 0
        },
        status: newStatus,
        ...(newStatus === 'rejected' && rejectionReason.trim() && { rejectionReason: rejectionReason.trim() })
      };
      
      // Use PUT request to the standard listings endpoint
      const res = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/listings/${id}`,
        payload,
        config
      );
      
      console.log('Status update response:', res.data);
      alert(`Listing ${newStatus} successfully!`);
      navigate('/admin/listings');
    } catch (err) {
      console.error('Error updating listing status:', {
        message: err.message,
        response: err.response?.data,
        status: err.response?.status,
      });
      setError(`Failed to update listing status: ${err.response?.data?.message || err.message}`);
      setActionLoading(false);
      setShowRejectionModal(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return 'https://via.placeholder.com/600x400?text=Property+Image';
    if (imagePath.startsWith('http') || imagePath.startsWith('data:')) return imagePath;
    return `${process.env.REACT_APP_API_URL}/uploads/${imagePath}`;
  };

  const openRejectModal = () => {
    setShowRejectionModal(true);
    setRejectionReason('');
  };

  const closeRejectModal = () => {
    setShowRejectionModal(false);
    setRejectionReason('');
  };

  if (loading) return <div className="container mx-auto px-4 py-8 text-center">Loading...</div>;
  if (error) return <div className="container mx-auto px-4 py-8 text-center">{error}</div>;
  if (!listing) return <div className="container mx-auto px-4 py-8 text-center">No listing data</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Review Listing: {listing.title}</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Main Content */}
        <div className="md:col-span-2">
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <h2 className="text-2xl font-semibold mb-4">Listing Details</h2>
            <img
              src={getImageUrl(listing.images && listing.images.length > 0 ? listing.images[0] : null)}
              alt={listing.title}
              className="w-full h-96 object-cover rounded-lg mb-4"
            />
            <p><strong>Location:</strong> {listing.location}</p>
            <p><strong>Price:</strong> ${listing.price}/night</p>
            <p><strong>Guests:</strong> {listing.guests}</p>
            <p><strong>Description:</strong> {listing.description}</p>
            <p><strong>Submitted by:</strong> {listing.createdBy?.name || 'Unknown'} ({listing.createdBy?.email || 'N/A'})</p>
            <p><strong>Submitted on:</strong> {new Date(listing.createdAt).toLocaleDateString()}</p>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-4">
            <button
              onClick={() => handleStatusChange('approved')}
              disabled={actionLoading}
              className="bg-green-500 text-white py-3 px-6 rounded-lg hover:bg-green-600 disabled:opacity-50"
            >
              {actionLoading ? 'Processing...' : 'Approve Listing'}
            </button>
            <button
              onClick={openRejectModal}
              disabled={actionLoading}
              className="bg-red-500 text-white py-3 px-6 rounded-lg hover:bg-red-600 disabled:opacity-50"
            >
              Reject Listing
            </button>
            <Link
              to="/admin/listings"
              className="bg-gray-500 text-white py-3 px-6 rounded-lg hover:bg-gray-600"
            >
              Back to Listings
            </Link>
          </div>

          {/* Rejection Warning */}
          <div className="mt-4 text-sm text-gray-600">
            <p>
              Approving this listing will make it visible to all users. If you reject
              this listing, the host will be notified via email.
            </p>
          </div>
        </div>

        {/* Quick Stats */}
        <div className="bg-gray-50 rounded-lg p-4">
          <h4 className="font-semibold mb-2">Quick Review Checklist</h4>
          <div className="space-y-2 text-sm">
            <div className="flex items-center">
              <span className="text-green-500 mr-2">✓</span>
              <span>Images provided: {listing.images?.length || 0}</span>
            </div>
            <div className="flex items-center">
              <span className="text-green-500 mr-2">✓</span>
              <span>Description complete</span>
            </div>
            <div className="flex items-center">
              <span className="text-green-500 mr-2">✓</span>
              <span>Pricing set</span>
            </div>
            <div className="flex items-center">
              <span className="text-green-500 mr-2">✓</span>
              <span>Location specified</span>
            </div>
          </div>
        </div>
      </div>

      {/* Rejection Modal */}
      {showRejectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md mx-4">
            <h3 className="text-xl font-semibold mb-4">Reject Listing</h3>
            <p className="text-gray-600 mb-4">
              Please provide a reason for rejecting this listing (optional):
            </p>
            <textarea
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              placeholder="e.g., Images are unclear, description needs more detail, pricing seems unrealistic..."
              className="w-full p-3 border rounded-lg h-24 resize-none"
            />
            <div className="flex gap-3 mt-4">
              <button
                onClick={() => handleStatusChange('rejected')}
                disabled={actionLoading}
                className="flex-1 bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 disabled:opacity-50"
              >
                {actionLoading ? 'Rejecting...' : 'Confirm Reject'}
              </button>
              <button
                onClick={closeRejectModal}
                disabled={actionLoading}
                className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListingReview;
